import { useState, useRef } from 'react';
import styled from 'styled-components';

interface TextAreaProps {
    /**
     * label description.
     */
    label?: string;

    /**
     * isRequired description.
     */
    isRequired?: boolean;

    /**
     * Textarea height in px.
     * @default '136'
     */
    baseHeight?: number;
}

interface WrapperProps {
    focused: boolean;
    expanded: boolean;
    height: number;
}

/**
 * Todo
 * Add focus outline
 * Add color variations
 */
const Wrapper = styled.div<WrapperProps>`
    max-width: 600px;
    height: ${({ height }) => `${height}px`};
    margin-bottom: 1rem;
    padding: 15px;
    border-radius: 6px;
    font-size: 16px;
    cursor: text;
    background-color: rgba(0, 0, 0, 0.3);
    transition: all 0.2s ease-in-out;
    display: flex;
    flex-direction: column;
    row-gap: 6px;

    ${({ focused, expanded }) =>
        (focused || expanded) &&
        `padding-top: 10px;
        border: 1px solid black; 
        background-color: var(--color-white);
        `}

    label {
        cursor: inherit;
        transition: inherit;

        ${({ focused, expanded }) =>
            (focused || expanded) &&
            `font-size: 12px;
            font-weight: bold;`}

        // required text
        i {
            font-style: normal;
            font-size: 12px;
            font-weight: normal;
            padding-left: 5px;
        }
    }

    textarea {
        all: unset;
        resize: none;
        flex-grow: 1;
        overflow: hidden;
        word-wrap: break-word;
        background-color: red;
    }
`;

/**
 * TextArea component
 */
export const TextArea = ({
    label,
    isRequired,
    baseHeight = 136,
}: TextAreaProps) => {
    const [focused, setFocused] = useState(false);
    const [expanded, setExpanded] = useState(false);
    const [height, setHeight] = useState(baseHeight);
    const textareaRef = useRef<HTMLTextAreaElement>(null);

    const onFocus = () => setFocused(true);
    const onBlur = () => setFocused(false);

    const handleTextArea = () => {
        if (!textareaRef.current) return;
        const textarea = textareaRef.current;

        setExpanded(textarea.value.length > 0);

        const lines = textarea.value.split('\n').length;
        const lineHeight = window.getComputedStyle(textarea).lineHeight;
        const fontSize = window.getComputedStyle(textarea).fontSize;
        const lineHeightPx = parseInt(lineHeight.replace('px', ''));
        const fontSizePx = parseInt(fontSize.replace('px', ''));
        const contentHeight = (lines * lineHeightPx) / fontSizePx;

        console.log({ lines, lineHeight, fontSize, contentHeight });

        console.log(contentHeight);
        setHeight(contentHeight < baseHeight ? baseHeight : contentHeight);
    };

    return (
        <Wrapper
            focused={focused}
            expanded={expanded}
            height={height}
            onClick={() => textareaRef?.current?.focus()}
        >
            {label && (
                <label>
                    {label}
                    {!isRequired && <i>(optional)</i>}
                </label>
            )}
            <textarea
                required={isRequired}
                onFocus={onFocus}
                onBlur={onBlur}
                onChange={handleTextArea}
                ref={textareaRef}
            ></textarea>
        </Wrapper>
    );
};
